                                <div className="flex flex-wrap gap-3">
                                    {state.topics.map((topic, i) => (
                                        <span key={i} className="bg-neutral-800 text-white px-4 py-2 rounded-full flex items-center space-x-2 border border-neutral-700">
                                            <span>{topic}</span>
